const client = require('client');
const http = require('http');
const fs = require('fs');
const cheerio = require('cheerio');
module.exports = (url) => {
    http.createServer((req, res) => {
        client(url, function (err, data) {
            if (err) {
                res.writeHead(500);
                res.end(err.message);
            } else {
                const $ = cheerio.load(data);
                // console.log($('td').text());
                fs.writeFile('./a.txt', $('td').text(), err => {
                    if (err) {
                        res.writeHead(500);
                        res.end(err.message);
                    } else {
                        res.writeHead(200, {
                            'Context-Type': 'text/plain;charset=utf-8'
                        });
                        res.end(data);
                    }
                });
            }
        });
    }).listen(8080);
};
